export function defineElement(name, extend = null) {
        console.log(getPrototype(name));
    
    global.customElements.define(
        extend || name,
        getPrototype(name),
        extend && { extends: name },
    );
}

function getPrototype(name) {
    return global.HTMLElement;
    // return document.createElement(name).constructor || global.HTMLElement;
}
